        throw e

    }
}



export const deleteCustomer = async (req, res,) => {

    try {
        let _id = req.params.id
        console.log(_id)

        const Customer = await customer.deleteOne({ _id: mongoose.Types.ObjectId(_id) })
         if(Customer){
            res.send({
                message:"document deleted bro!"
            })
         }

    }
